#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <stdbool.h>
#include <stdint.h>

#define INVALID_SWAP -2
#define INVALID_IDX -3
#define INVALID_SUBSTR_LEN -4
#define SUBSTR_NOT_FOUND -5

// Swap characters of s at indices idx1 and idx2
long swap(char* s, unsigned long idx1, unsigned long idx2){
    // Perform swap
    char tmp = s[idx1];
    s[idx1] = s[idx2];
    s[idx2] = tmp;

    return 0;
}

// Count number of specific characters in string
void countChar(char* s, uint64_t len, uint64_t* counts){
    counts[0] = 0; // stores number of '0'
    counts[1] = 0; // stores number of '1'
    for(uint64_t i = 0; i < len; i++){
        if (s[i] == '0') counts[0] += 1;
        if (s[i] == '1') counts[1] += 1;
    }
    return;
}

// Return index of right-most zero, starting from start from the beginning
uint64_t rightMostZero(char* s, uint64_t len, uint64_t start){
    for (uint64_t i = start; i >= 0; i--){
        if (s[i] == '0'){
            return i;
        }
    }
    return -1;
}

// Return index of left-most one, starting from start from the beginning
uint64_t leftMostOne(char* s, uint64_t len, uint64_t start){
    for (uint64_t i = start; i < len; i++){
        if (s[i] == '1'){
            return i;
        }
    }
    return -1;
}

long long minimumSteps(char* s) {
    // Get string length
    uint64_t len = strlen(s);
    if(len <= 1) return 0;

    // Count number of ones and zeros
    uint64_t counts[2] = {0, 0};
    countChar(s, len, counts);
    if(counts[0] == 0 || counts[1] == 0) return 0;

    //printf("counts: %d %d\n", counts[0], counts[1]);

    // Create expected "final" string
    char sol[len+1];
    memset(sol, '0', len);
    memset(&sol[counts[0]], '1', counts[1]);
    sol[len] = '\0';

    // Allocate string we can manipulate: s1
    char* s1 = (char *) malloc(strlen(s)+1);
    memcpy(s1, s, strlen(s)+1);

    //printf("Input: %s\n", s1);
    //printf("Expecting: %s\n", sol);

    // Keep swapping left-most "1" with right-most "0"
    unsigned long long swapCount = 0;
    unsigned long leftStart = 0;
    unsigned long rightStart = len-1;
    while (1) {
        //printf("current (%d): %s\n", swapCount, s1);
        uint64_t leftOneIdx = leftMostOne(s1, len, leftStart);
        uint64_t rightZeroIdx = rightMostZero(s1, len, rightStart);
        //printf("%lu, %lu, diff %ld\n", leftOneIdx, rightZeroIdx, (int64_t) (rightZeroIdx - leftOneIdx));
        //printf("count: %lu\n", swapCount);
        if (swapCount < 0) break;
        if(leftOneIdx >= rightZeroIdx) break;
        swap(s1, leftOneIdx, rightZeroIdx);
        swapCount += rightZeroIdx - leftOneIdx;
        leftStart = leftOneIdx + 1;
        rightStart = rightZeroIdx - 1;
    };

    return swapCount;
}

int main(){
    char* str = "";
    char* s = (char *) malloc(strlen(str)+1);
    memcpy(s, str, strlen(str)+1);

    //printf("Input: %s\n", s);
    long out = minimumSteps(s);
    printf("Output: %d\n", out);

    return 0;
}